mapper.update:
update se_menu
id = #{id}, menucode = #{menucode}, parentcode = #{parentcode}, captionname = #{captionname}, menuorder = #{menuorder}, sourceurl = #{sourceurl}, classname = #{classname}, visible = #{visible}, entrytime = #{entrytime}, updatetime = #{updatetime}, updateid = #{updateid},
where id = #{id}

mapper.select:

select 
	id,
	menucode,
	parentcode,
	captionname,
	menuorder,
	sourceurl,
	classname,
	visible,
	entrytime,
	updatetime,
	updateid
from se_menu

	 and id = #{id} 
	 and menucode = #{menucode} 
	 and parentcode = #{parentcode} 
	 and captionname = #{captionname} 
	 and menuorder = #{menuorder} 
	 and sourceurl = #{sourceurl} 
	 and classname = #{classname} 
	 and visible = #{visible} 
	 and entrytime = #{entrytime} 
	 and updatetime = #{updatetime} 
	 and updateid = #{updateid}


